// Exemplo de animações com GSAP + ScrollTrigger
// Certifique-se de que ScrollTrigger está incluída na build ou script do GSAP que você baixou.

window.addEventListener('load', () => {
    // Registro do plugin ScrollTrigger (caso esteja usando GSAP separado).
    if (gsap && gsap.registerPlugin) {
      gsap.registerPlugin(ScrollTrigger);
    }
  
    // Animação de entrada do header
    gsap.from(".site-header", {
      duration: 1,
      y: -70,
      opacity: 0,
      ease: "power2.out"
    });
    
    // Hero section: Título e parágrafo
    gsap.from(".hero-content h1", {
      duration: 1,
      x: -50,
      opacity: 0,
      ease: "power2.out"
    });
    
    gsap.from(".hero-content p", {
      duration: 1,
      x: 50,
      opacity: 0,
      ease: "power2.out",
      delay: 0.5
    });
  
    // Efeito de hover no botão CTA
    const ctaButton = document.querySelector('.cta-button');
    if (ctaButton) {
      ctaButton.addEventListener('mouseenter', () => {
        gsap.to(ctaButton, {
          scale: 1.1,
          duration: 0.2,
          ease: "power2.out"
        });
      });
  
      ctaButton.addEventListener('mouseleave', () => {
        gsap.to(ctaButton, {
          scale: 1,
          duration: 0.2,
          ease: "power2.out"
        });
      });
    }
  
    // Animação dos destaques do blog com ScrollTrigger
    gsap.from(".blog-highlights h2", {
      scrollTrigger: {
        trigger: ".blog-highlights",
        start: "top 80%",
        end: "bottom 60%",
        toggleActions: "play none none reverse",
      },
      duration: 1,
      y: 30,
      opacity: 0,
      ease: "power2.out"
    });
    
    // Anima cada destaque (card) do blog
    const highlights = document.querySelectorAll(".highlight");
    highlights.forEach((highlight, index) => {
      gsap.from(highlight, {
        scrollTrigger: {
          trigger: highlight,
          start: "top 90%",
        },
        duration: 0.8,
        opacity: 0,
        y: 30,
        delay: index * 0.2
      });
    });
  });

